<template>
	<view class="wrap">
		<!-- <u-button @click="clear">清空列表</u-button> -->
		<u-waterfall v-model="flowList" ref="uWaterfall">
			<template v-slot:left="{ leftList }">
				<view class="demo-warter" v-for="(item, index) in leftList" :key="index">
					<!-- 警告：微信小程序中需要hx2.8.11版本才支持在template中结合其他组件，比如下方的lazy-load组件 -->
					<u-lazy-load threshold="-450" border-radius="10" :image="item.url" :index="index"></u-lazy-load>
					<view class="demo-title">{{ item.name }}</view>
					<u-icon name="close-circle-fill" color="#fa3534" size="34" class="u-close" @click="remove(item.id)"></u-icon>
				</view>
			</template>
			<template v-slot:right="{ rightList }">
				<view class="demo-warter" v-for="(item, index) in rightList" :key="index">
					<u-lazy-load threshold="-450" border-radius="10" :image="item.url" :index="index"></u-lazy-load>
					<view class="demo-title">{{ item.name }}</view>
					<u-icon name="close-circle-fill" color="#fa3534" size="34" class="u-close" @click="remove(item.id)"></u-icon>
				</view>
			</template>
		</u-waterfall>
		<u-loadmore bg-color="rgb(240, 240, 240)" :status="loadStatus" @loadmore="addRandomData"></u-loadmore>
	</view>
</template>

<script>
export default {
	data() {
		return {
			loadStatus: 'loadmore',
			flowList: [],
			list: [
				{
					name: '默认标题名',
					url: 'data:image/jpeg;base64,',
				}
			]
		}
	},
	onLoad() {
		// this.addRandomData();
		let that = this
		let db = uniCloud.database();
		db.collection('files').get().then(res=>{
			that.list = res.result.data
			that.list.forEach(item=>{
				that.flowList.push(item)
			})
		})
	},
	onReachBottom() {
		// this.loadStatus = 'loading';
		// // 模拟数据加载
		// setTimeout(() => {
		// 	this.addRandomData();
		// 	this.loadStatus = 'loadmore';
		// }, 1000)
	},
	methods: {
		 	isImage(str) {
					var reg = /w(.png|.jpg|.gif|.bmp|.psd|.tiff|.tga|.eps)/i;
					return str.match(reg);
				},
		addRandomData() {
			for(let i = 0; i < 10; i++) {
				let index = this.$u.random(0, this.list.length - 1);
				// 先转成字符串再转成对象，避免数组对象引用导致数据混乱
				let item = JSON.parse(JSON.stringify(this.list[index]))
				item.id = this.$u.guid();
				this.flowList.push(item);
			}
		},
		remove(id) {
			this.$refs.uWaterfall.remove(id);
		},
		// clear() {
		// 	this.$refs.uWaterfall.clear();
		// }
	}
}
</script>

